import { createRouter, createWebHistory } from 'vue-router';
import MenuPage from '../components/MenuPage.vue'; // Halaman utama menu produk
import ProductDetail from '../components/ProductDetail.vue'; // Halaman detail produk
import Cart from '../components/Cart.vue'; // Halaman keranjang
import Payment from '../components/Payment.vue'; // Halaman pembayaran
import Login from '../components/Login.vue'; // Halaman login
import Register from '../components/Register.vue'; // Halaman register

// Menentukan rute aplikasi
const routes = [
  { path: '/', component: Menu }, // Halaman utama menampilkan produk
  { path: '/product/:id', component: ProductDetail }, // Halaman detail produk
  { path: '/cart', component: Cart }, // Halaman keranjang
  { path: '/payment', component: Payment }, // Halaman pembayaran
  { path: '/login', component: Login }, // Halaman login
  { path: '/register', component: Register } // Halaman register
];

// Membuat instance router
const router = createRouter({
  history: createWebHistory(),
  routes
});

export default router;
